import axios from "axios";
import $ from 'jquery';
export default {
  name: "ImageView",
  data: function () {
    return {
      restorani: []
    };
  },
  mounted: function () {
    $(document).ready(function () {
      $(".search").keyup(function () {
        var searchTerm = $(".search").val();
        var listItem = $('.results tbody').children('tr');
        var searchSplit = searchTerm.replace(/ /g, "'):containsi('");
        $.extend($.expr[':'], {
          'containsi': function (elem, i, match, array) {
            return (elem.textContent || elem.innerText || '').toLowerCase().indexOf((match[3] || "").toLowerCase()) >= 0;
          }
        });
        $(".results tbody tr").not(":containsi('" + searchSplit + "')").each(function (e) {
          $(this).attr('visible', 'false');
        });
        $(".results tbody tr:containsi('" + searchSplit + "')").each(function (e) {
          $(this).attr('visible', 'true');
        });
        var jobCount = $('.results tbody tr[visible="true"]').length;
        $('.counter').text(jobCount + ' item');

        if (jobCount == '0') {
          $('.no-result').show();
        } else {
          $('.no-result').hide();
        }
      });
    });
    axios.get("http://localhost:8081/api/restorani").then(res => {
      console.log(res.data);
      this.restorani = res.data;
    }).catch(err => {
      // todo neovlascen pristup
      console.log(err);
    });
  },
  methods: {
    seeMore: function (naziv) {
      // ovde dobije employee, ali ima delova koda kao sto je employeesComp koji barata sa employee a ne znam odakle mu
